function earlyver();% earlyver - name of most recently zipwork update file used @ startup%    earlyver displays the name of the Winzip file most recently unpacked%    during startup.%%    See also EarlyUpgrade. UpgradeLog = fullfile(prefdir(1), 'EARLY_update.log');if exist(UpgradeLog, 'file'),    TT = mytextread(UpgradeLog);    TT = words2cell(TT{end}, ' ');    WZ = TT{end};    unzipdate = cell2words(TT(1:end-2));    disp([WZ ' (unzipped ' unzipdate ')']);else,    disp('No logged Upgrade found.');    WZ = '';endif isequal('marcel', lower(username)),    SD = fromsetupFile('backup', 'USBstickDrive');    WZdir = fullfile(SD, 'ZippedWork');    if exist(WZdir, 'dir'), % check if up to date        qq = dir(fullfile(WZdir, '*}EARLY*.zip'));        qq = sortaccord(qq, [qq.datenum]);        WZusb = qq(end).name;        if isequal(WZusb, WZ)             disp('------USB zipfile already unpacked ------');        elseif ~isempty(strfind(WZusb, ['{' compuname '}'])), % 2nd option: zipfile was made on this computer            disp(['------most recent USB zipfile was made on this computer: ' WZusb]);        else,            disp(' ');            disp(['>>>>>>>>>>>>USB stick has (newer?) Winzip file<<<<<<<<<<<<<<<']);            disp(['       ' WZusb]);            disp(['>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>><<<<<<<<<<<<<<<<<<<<<<<<<<<<<']);        end        disp(' ');    else,        disp('   (No zipout dir found on USB stick.)');    endend%===info of file dates in pathqq = rdir(versiondir);qq = sortAccord(qq, [qq.datenum]); qqm = qq(strmatch('.m', {qq.ext}, 'exact'));qqrcx = qq(strmatch('.rcx', {qq.ext}, 'exact'));qqdoc  = qq(strmatch('.doc', {qq.ext}, 'exact'));qqtxt  = qq(strmatch('.txt', {qq.ext}, 'exact'));qqc  = qq(strmatch('.c', {qq.ext}, 'exact'));display('=======Most recent========')dinfo = @(d)disp([d(end).date  '  ' d(end).name '  in ' d(end).folder ]);dinfo(qqm);dinfo(qqdoc);dinfo(qqrcx);dinfo(qqtxt);dinfo(qqc);