function varargout=CLUT(varargin)% Obsolete: CLUT has been renamed Attenuator. % 4/17/02 dgp Wrote it.switch nargoutcase 0;	Attenuator(varargin{:});case 1; [varargout{1}]=Attenuator(varargin{:});case 2; [varargout{1},varargout{2}]=Attenuator(varargin{:});case 3; [varargout{1},varargout{2},varargout{3}]=Attenuator(varargin{:});case 4; [varargout{1},varargout{2},varargout{3},varargout{4}]=Attenuator(varargin{:});case 5; [varargout{1},varargout{2},varargout{3},varargout{4},varargout{5}]=Attenuator(varargin{:});case 6; [varargout{1},varargout{2},varargout{3},varargout{4},varargout{5},varargout{6}]=Attenuator(varargin{:});otherwise error('CLUT.m: too many output args.');end