% ArcTest%% Compare the boundaries of arcs drawn by Screen 'FrameArc' and 'FillArc'% 4/23/02   awi wrote it[w,rect]=Screen(0,'OpenWindow',128,[],32);Screen(w,'TextFont','Arial');Screen(w,'TextSize',40);Screen(w,'DrawText','Click the mouse to proceed',100,100,[0,255,0]);Screen(w,'FrameArc',[255 0 0],CenterRect([0,0,200,200],rect),0,90);GetClicks;Screen(w,'FillArc',255,CenterRect([0,0,200,200],rect),0,90);GetClicks;Screen(w,'FrameArc',[255 0 0],CenterRect([0,0,202,202],rect),0,90);GetClicks;Screen(w,'FillArc',128,CenterRect([0,0,202,202],rect),0,90);GetClicks;Screen(w,'Close');